import React, { useEffect, useRef, useState } from 'react'
import Airdrop from './Airdrop';
import Balances from './Balances'
import usdt from './usdt-logo.png'

const Main = ({
  stakingBalance,
  tetherBalance,
  rwdBalance,
  stakeTokens,
  withdrawTokens,
  airdropTime,
  setAirdropTime
}) => {
  const inputRef = useRef(null);

  const handleMaxClick = () => {
    inputRef.current.value = window.web3.utils.fromWei(tetherBalance, 'ether');
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    let amount;
    amount = inputRef.current.value.toString();
    amount = window.web3.utils.toWei(amount, 'ether');
    stakeTokens(amount);
  }

  useEffect(() => {
    if(airdropTime === 0) {
      return ;
    }
    const interval = setInterval(() => {
      setAirdropTime(airdropTime - 1);
    }, 1000);
    return () => clearInterval(interval);
  }, [airdropTime]);

  return (
    <div className='widget-container'>
      <Balances
        stakingBalance={stakingBalance}
        rewardBalance={rwdBalance} />
      <form
        onSubmit={(e) => handleSubmit(e)}
        className='form'>
          <div className='input-wrapper'>
            <img src={usdt} className="usdt" alt="usdt" />
            <input ref={inputRef} className="input" type="text" placeholder="Amount to stake" />
            <div className='amount-wrapper'>
              <span>{window.web3.utils.fromWei(tetherBalance, 'Ether')} Available</span>
              <div className='max' onClick={handleMaxClick}>MAX</div>
            </div>
          </div>
          <div className='buttons'>
            <button className='btn deposit' type="submit">Deposit</button>
            <button className='btn withdraw' onClick={withdrawTokens}>Withdraw</button>
          </div>
      </form>
      <Airdrop airdropTime={airdropTime}/>
    </div>
  )
}

export default Main
